catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Get the values of the start date and expiry date fields
    var startDate = g_form.getValue('start_date'); // Replace with the correct field name for Start Date
    var expiryDate = g_form.getValue('expiry_date'); // Replace with the correct field name for Expiry Date

    if (startDate && expiryDate) {
        var startDateObj = new Date(startDate);
        var expiryDateObj = new Date(expiryDate);

        // Calculate the difference in years between the start date and expiry date
        var differenceInYears = (expiryDateObj - startDateObj) / (365.25 * 24 * 60 * 60 * 1000);

        // Validate if Expiry Date is more than 10 years after Start Date
        if (differenceInYears > 10) {

            g_form.setValue('expiry_date', ''); // Clear the Expiry Date field
			g_form.showFieldMsg('expiry_date', 'Expiry Date cannot be more than 10 years from the Start Date', 'error');
        } 
		else {
            g_form.hideFieldMsg('expiry_date'); // Remove any existing messages if the date is valid
        }
    }
}
