catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Get the values of the two date fields
    var startDate = g_form.getValue('start_date'); // Replace with the correct field name for Start Date
    var constructionStart = g_form.getValue('construction_start_date'); // Replace with the correct field name for Construction Start


    // Check if both dates are populated
    if (startDate && constructionStart) {
        var startDateObj = new Date(startDate);
        var constructionStartObj = new Date(constructionStart);

        // Validate if Construction Start is before Start Date
        if (constructionStartObj < startDateObj) {



            g_form.setValue('construction_start_date', ''); // Clear the Construction Start date field
            g_form.showFieldMsg('construction_start_date', 'Construction Start date cannot be before the Start Date', 'error');

        } else {
            g_form.hideFieldMsg('construction_start_date'); // Remove any existing messages if the date is valid
        }
    }
}
