catalog_script_client.script:
function onLoad() {
    // Ensure the DOM is ready before attempting to modify it
    setTimeout(addHelpText, 500); // Delay to ensure form elements are available
}

function addHelpText() {
    // Get the form element for the description field
    var descriptionField = g_form.getFormElement('title_of_proposal');

    if (descriptionField) {
        // Check if help text is already added to avoid duplicates
        if (descriptionField.nextSibling && descriptionField.nextSibling.className === 'custom-help-text') {
            return;
        }

        // Create a new div for help text
        var helpTextDiv = document.createElement('div');
        helpTextDiv.className = 'custom-help-text'; // Custom class for styling
        helpTextDiv.innerHTML = 'This is the help text that provides additional information about the description field.';

        // Insert the help text after the description field
        descriptionField.parentNode.insertBefore(helpTextDiv, descriptionField.nextSibling);
    } else {

    }g_form.addInfoMessage('Description field not found.');
}

// Add custom CSS for the help text
(function() {
    var style = document.createElement('style');
    style.innerHTML = `
        .custom-help-text {
            color: #007cba; /* Custom color for the help text */
            font-size: 12px;
            margin-top: 5px;
        }
    `;
    document.head.appendChild(style);
})();
