sys_variable_value.value:
(function execute(inputs, outputs) {
    // Inputs
    var groupIds = inputs.groups; // List of group Sys IDs (comma-separated string)
    var request = inputs.request; // Request record Sys ID

    // Ensure that groupIds is treated as a string, then split it into an array
    var groupArray = groupIds.split(',');

    // Iterate over each group ID in the array and create approval requests
    groupArray.forEach(function(groupId) {
        var grGroup = new GlideRecord('sys_user_group');
        if (grGroup.get(groupId.trim())) {
            var grUser = new GlideRecord('sys_user');
            grUser.addQuery('sys_id', 'IN', grGroup.getValue('members')); // Get members of the group
            grUser.query();
            
            while (grUser.next()) {
                var approval = new GlideRecord('sysapproval_approver');
                approval.initialize();
                approval.setValue('source_table', 'your_request_table'); // Replace with your table
                approval.setValue('source_sys_id', request);
                approval.setValue('user', grUser.sys_id);
                approval.setValue('state', 'requested'); // Set initial state to 'Requested'
                approval.setValue('comments', 'Approval request for ' + request);
                approval.insert();
            }
        }
    });

    // Output a success message
    outputs.successMessage = 'Approval requests sent to all selected groups.';
})(inputs, outputs);

