    }

    // Query for Task 2 based on task type
    var task2 = new GlideRecord('x_1329978_new_la_0_lup_task_table');
    task2.addQuery('task_type', 'Document Control Quality Check');
    task2.addQuery('parent', current.parent);
    task2.query();

    var task2Closed = false;
    if (task2.next()) {
        gs.info('Task 2 found: ' + task2.number + ', State: ' + task2.state);
        task2Closed = (task2.state == '3'); // Replace '3' if the state value is different
    } else {
        gs.info('Task 2 (Document Control Quality Check) not found.');
    }

    // Check if both Task 1 and Task 2 are closed
    if (!task1Closed || !task2Closed) {
        gs.info('Task 3 cannot be closed because Task 1 or Task 2 is not closed.');
