sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Construct encoded query string for tasks that are not yet closed

    // var tasks3 = 'short_descriptionINGIS Validation,Document Control Quality Check,Final Decision^lup_request=' + current.lup_request;


	var encodedQuery = 'short_descriptionINGIS Validation,Document Control Quality Check,^lup_request=' + current.lup_request;

    var grTask = new GlideRecord('x_1329978_new_la_0_lup_task_table');
    grTask.addEncodedQuery(encodedQuery);
    grTask.query();

    var notClosedTasks = [];

    while (grTask.next()) {
        var taskDescription = grTask.getValue('short_description'); // Access field value
        var taskState = grTask.getValue('state'); // Access the state value

        // Check if the task is not closed and the short description is not already in the array
        if (taskState != '3' && taskDescription && !notClosedTasks.includes(taskDescription)) {
            notClosedTasks.push(taskDescription);
        }



        // var taskDescription = grTask.short_description; // Access field value
        // if (taskDescription) {
        //     if (grTask.state != '3') { // Assuming '3' is the state value for 'Closed'
        //         notClosedTasks.push(taskDescription);
        //     }
        // } else {
        //     gs.info('Task record with Sys ID ' + grTask.sys_id + ' has no short_description.');
        // }
    }

    // If there are tasks that are not closed, show an error message
    if (notClosedTasks.length > 0) {
        var errorMessage = 'You cannot close this task until the following tasks are closed: ' + notClosedTasks.join(', ') + '.';
        gs.addErrorMessage(errorMessage);
        current.setAbortAction(true); // Prevent the current task from being closed
    } else {
        gs.info('All required tasks are closed.');
    }
})(current, previous);
