sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Query for Task 1
    var task1 = new GlideRecord('x_1329978_new_la_0_lup_task_table');
    task1.addQuery('short_description', 'GIS Validation');
    task1.addQuery('parent', current.lup_request); // Ensure it's the same parent request
    task1.query();

    var task1Closed = false;
    if (task1.next()) {
        task1Closed = (task1.state == '3');
        gs.info('Task 1 Closed: ' + task1Closed);
    } else {
        gs.info('Task 1 not found.');
    }

    // Query for Task 2
    var task2 = new GlideRecord('x_1329978_new_la_0_lup_task_table');
    task2.addQuery('short_description', 'Document Control Quality Check');
    task2.addQuery('parent', current.lup_request); // Ensure it's the same parent request
    task2.query();

    var task2Closed = false;
    if (task2.next()) {
        task2Closed = (task2.state == '3');
        gs.info('Task 2 Closed: ' + task2Closed);
    } else {
        gs.info('Task 2 not found.');
    }

    // Check if both Task 1 and Task 2 are closed
    if (!task1Closed || !task2Closed) {
        gs.addErrorMessage('You cannot close Task 3 until both Task 1 and Task 2 are closed.');
        current.setAbortAction(true); // Prevent Task 3 from being closed
    }

})(current, previous);
