sys_script_client.script:
function onSubmit() {
    // Get the value of the 'outcome_of_review' field
    var outcomeOfReview = g_form.getValue('outcome_of_review');

    // Get the value of the 'state' field
    var state = g_form.getValue('state');

    // Check if the outcome of the review is "Return to Applicant" and the state is being set to "Closed"
    if (outcomeOfReview === 'Returned to Applicant' && state === '3') { // Assuming '3' is the value for "Closed"
        // Show an error message and prevent form submission
        g_form.addErrorMessage('The request cannot be closed because the outcome of the review is "Return to Applicant".');
        return false; // Prevent the form submission
    }

    // Allow form submission
    return true;
}
