sys_script_client.script:
function onLoad() {
    var tableName = g_form.getTableName();
    var sectionLabel = 'Task notes'; // Replace with the exact display name of your section

    var ga = new GlideAjax('FieldManagerForSection');
    ga.addParam('sysparm_name', 'getFields');
    ga.addParam('sysparm_tableName', tableName);
    ga.addParam('sysparm_sectionLabel', sectionLabel);

    ga.getXMLAnswer(function(response) {
        var fields = JSON.parse(response);
        if (fields.length > 0) {
            g_form.addInfoMessage('Fields found: ' + fields.join(', '));
            for (var i = 0; i < fields.length; i++) {
                var field = fields[i];
                g_form.setMandatory(field, true); // Make each field mandatory
            }
        } else {
            g_form.addErrorMessage('No fields found for the section: ' + sectionLabel);
        }
    });
}
