sys_script_client.script:
function onLoad() {
    // Define the section name (replace with the actual section backend name)
    var sectionName = 'document_controlqualitycheck'; 

    // Retrieve the fields of the specified section
    var sectionFields = getSectionFields(sectionName);

    // Show the fields in the section as an informational message
    g_form.addInfoMessage('Fields in section "' + sectionName + '": ' + sectionFields.join(', '));
alert('Fields in section "' + sectionName + '": ' + sectionFields.join(', '));

}

// Helper function to get all fields in a section
function getSectionFields(sectionName) {
    var sectionFields = [];
    var allFields = g_form.getFormElements(); // Get all form elements

    for (var i = 0; i < allFields.length; i++) {
        var fieldName = allFields[i].name; // Field name
        var fieldElement = g_form.getControl(fieldName);

        if (fieldElement) {
            // Check if the field belongs to the given section
            if (isFieldInSection(fieldElement, sectionName)) {
                sectionFields.push(fieldName);
            }
        }
    }

    return sectionFields;
}

// Helper function to determine if a field is in a specific section
function isFieldInSection(fieldElement, sectionName) {
    // Example logic to determine if the field belongs to the section
    var parentSection = fieldElement.closest('.section'); // Adjust this based on your HTML structure

    // Check if the parent section has a data attribute or class for identification
    if (parentSection && parentSection.getAttribute('data-section') === sectionName) {
        return true;
    }

    return false;
}
