sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // The value of the "task_type" field
    var stageValue = g_form.getValue('task_type'); // Ensure 'task_type' is the correct field name

    // Control the visibility of the "document_controlqualitycheck" section
    if (stageValue === 'document control quality check') {
        g_form.setSectionDisplay('document_controlqualitycheck', true);
        g_form.setSectionDisplay('presubmission', false);
        g_form.setSectionDisplay('technical_informationandpresentation', false);
    } 
    // Control the visibility of the "presubmission" section
    else if (stageValue === 'pre-submission') {
        g_form.setSectionDisplay('presubmission', true);
        g_form.setSectionDisplay('document_controlqualitycheck', false);
        g_form.setSectionDisplay('technical_informationandpresentation', false);
    } 
    // Control the visibility of the "technical_informationandpresentation" section
    else if (stageValue === 'technical information and presentation content') {
        g_form.setSectionDisplay('technical_informationandpresentation', true);
        g_form.setSectionDisplay('document_controlqualitycheck', false);
        g_form.setSectionDisplay('presubmission', false);
    } 
    // Hide all sections if "task_type" does not match any condition
    else {
        g_form.setSectionDisplay('document_controlqualitycheck', false);
        g_form.setSectionDisplay('presubmission', false);
        g_form.setSectionDisplay('technical_informationandpresentation', false);
    }
}
