sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // The value of the "Stage" field
    var stageValue = g_form.getValue('task_type'); // Replace 'stage' with the correct field name for the stage

    // Define the section name to hide
    var sectionName = 'presubmission'; // Replace with the actual backend name if different

    // Check the stage value and hide the section accordingly
    if (stageValue === 'pre-submission') { // Replace 'pre-submission' with the actual value that triggers the hide
        g_form.setSectionDisplay(sectionName, true);
    } else {
        g_form.setSectionDisplay(sectionName, false);
    }
}
