sys_script_include.script:
var FieldManagerForSection = Class.create();
FieldManagerForSection.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getFields: function() {
        var tableName = this.getParameter('sysparm_tableName');
        var sectionLabel = this.getParameter('sysparm_sectionLabel');

        // Ensure the query parameters are being logged for debugging
        gs.info('Table: ' + tableName + ', Section Label: ' + sectionLabel);

        var sectionGr = new GlideRecord('sys_ui_section');
        sectionGr.addQuery('caption', sectionLabel);
        sectionGr.addQuery('view', 'Default view'); // Adjust as needed
        sectionGr.addQuery('table', tableName);
        sectionGr.query();

        if (sectionGr.next()) {
            gs.info('Section found: ' + sectionGr.name);
            var fields = [];
            var elementGr = new GlideRecord('sys_ui_element');
            elementGr.addQuery('sys_ui_section', sectionGr.sys_id);
            elementGr.orderBy('position');
            elementGr.query();

            while (elementGr.next()) {
                fields.push(elementGr.element.toString());
            }
            return JSON.stringify(fields);
        } else {
            gs.info('No section found for label: ' + sectionLabel);
            return JSON.stringify([]);
        }
    },

    type: 'FieldManagerForSection'
});
