sys_script_include.script:
var GetFieldsInSection = Class.create();
GetFieldsInSection.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getFields: function() {
        var tableName = this.getParameter('sysparm_tableName');
        var sectionLabel = this.getParameter('sysparm_sectionLabel');

        var sectionGr = new GlideRecord('sys_ui_section');
        sectionGr.addQuery('caption', sectionLabel);
        sectionGr.addQuery('view', 'Default view'); // Adjust the view if necessary
        sectionGr.addQuery('table', tableName);
        sectionGr.query();

        if (sectionGr.next()) {
            var fields = [];
            var elementGr = new GlideRecord('sys_ui_element');
            elementGr.addQuery('sys_ui_section', sectionGr.sys_id);
            elementGr.orderBy('position'); // Orders the fields by their position in the section
            elementGr.query();

            while (elementGr.next()) {
                fields.push(elementGr.element.toString());
            }
            return JSON.stringify(fields);
        }
        return JSON.stringify([]);
    },

    type: 'GetFieldsInSection'
});
