sys_script_include.script:
var UserCompanyDetails = Class.create();
UserCompanyDetails.prototype = {
    initialize: function() {},

    getCompanyDetails: function(userSysId) {
        var result = {};

        // Fetch the user record
        var grUser = new GlideRecord('sys_user');
        if (grUser.get(userSysId)) {
            var companySysId = grUser.getValue('company');
            if (companySysId) {
                result.company = companySysId;

                // Fetch the company record to get the head and head's email
                var grCompany = new GlideRecord('core_company');
                if (grCompany.get(companySysId)) {
                    result.companyHead = grCompany.getValue('company_head_field_name');

                    if (result.companyHead) {
                        // Fetch the company head record to get their email address
                        var grHead = new GlideRecord('sys_user');
                        if (grHead.get(result.companyHead)) {
                            result.headEmail = grHead.getValue('email');
                        }
                    }
                }
            }
        }
        return JSON.stringify(result);
    },

    type: 'UserCompanyDetails'
};
